import groovy.json.JsonOutput
import groovy.json.JsonSlurper
import org.sonatype.nexus.blobstore.api.BlobStoreManager
import org.sonatype.nexus.repository.maven.LayoutPolicy
import org.sonatype.nexus.repository.maven.VersionPolicy
import org.sonatype.nexus.repository.storage.WritePolicy
security.setAnonymousAccess(true)
security.getAnonymousManager().getConfiguration().setRealmName('NexusAuthenticatingRealm')
def response = [newUser: false]
def request = new JsonSlurper().parseText(args)
try {
    security.getSecuritySystem().getUser(request.username as String)
} catch (ignored) {
    security.addUser(request.username as String, 'John', 'Doe', 'john-doe@noemail.com', true, request.password as String, ['Admins', 'nx-admin'])
    response.newUser = true
}
if (!repository.getRepositoryManager().get('maven-central')) {
    repository.createMavenProxy('maven-central', 'https://repo1.maven.org/maven2/', BlobStoreManager.DEFAULT_BLOBSTORE_NAME, true, VersionPolicy.RELEASE, LayoutPolicy.PERMISSIVE)
}
if (!repository.getRepositoryManager().get('maven-snapshots')) {
    repository.createMavenHosted('maven-snapshots', BlobStoreManager.DEFAULT_BLOBSTORE_NAME, true, VersionPolicy.SNAPSHOT, WritePolicy.ALLOW, LayoutPolicy.PERMISSIVE)
}
if (!repository.getRepositoryManager().get('maven-releases')) {
    repository.createMavenHosted('maven-releases', BlobStoreManager.DEFAULT_BLOBSTORE_NAME, true, VersionPolicy.RELEASE, WritePolicy.ALLOW_ONCE, LayoutPolicy.STRICT)
}
if (!repository.getRepositoryManager().get('maven-public')) {
    def mavenGroup = ['maven-central', 'maven-snapshots', 'maven-releases']
    repository.createMavenGroup('maven-public', mavenGroup, BlobStoreManager.DEFAULT_BLOBSTORE_NAME)
}
if (!repository.getRepositoryManager().get('docker')) {
    repository.createDockerHosted('docker', 5000, null, BlobStoreManager.DEFAULT_BLOBSTORE_NAME, true, true, WritePolicy.ALLOW)
}
if (!repository.getRepositoryManager().get('npmjs')) {
    repository.createNpmProxy('npmjs', 'https://registry.npmjs.org', BlobStoreManager.DEFAULT_BLOBSTORE_NAME)
}
if (!repository.getRepositoryManager().get('npm-registry')) {
    repository.createNpmHosted('npm-registry', BlobStoreManager.DEFAULT_BLOBSTORE_NAME, true, WritePolicy.ALLOW_ONCE)
}
if (!repository.getRepositoryManager().get('npm')) {
    def npmGroup = ['npmjs', 'npm-registry']
    repository.createNpmGroup('npm', npmGroup, BlobStoreManager.DEFAULT_BLOBSTORE_NAME)
}
return JsonOutput.toJson(response)
